import React, { useEffect, useState } from 'react';
import './Logs.css';

function Logs() {
    const [logs, setLogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchLogs();
        // Atualiza os logs a cada 5 segundos
        const interval = setInterval(fetchLogs, 5000);
        return () => clearInterval(interval);
    }, []);

    const fetchLogs = async () => {
        try {
            const response = await fetch('/api/executions');
            if (!response.ok) {
                throw new Error('Erro ao buscar logs');
            }
            const data = await response.json();
            setLogs(data);
            setLoading(false);
        } catch (error) {
            console.error('Erro ao buscar logs:', error);
            setError(error.message);
            setLoading(false);
        }
    };

    if (loading) {
        return <div className="logs">Carregando...</div>;
    }

    if (error) {
        return <div className="logs">Erro: {error}</div>;
    }

    return (
        <div className="logs">
            <h2>Logs do Sistema</h2>
            <div className="logs-list">
                {logs.length === 0 ? (
                    <p>Nenhum log encontrado</p>
                ) : (
                    logs.map((log) => (
                        <div key={log.id} className="log-item">
                            <div className="log-header">
                                <span className="log-timestamp">
                                    {new Date(log.executedAt).toLocaleString()}
                                </span>
                                <span className={`log-status ${log.status.toLowerCase()}`}>
                                    {log.status}
                                </span>
                            </div>
                            <h3>{log.scheduleName}</h3>
                            <p className="log-message">
                                Agendamento #{log.scheduleId} - Resposta: {log.response}
                            </p>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default Logs;
